DECLARE @DATA TABLE (BF_SSN CHAR(9), LOAN_ID VARCHAR(50))

SELECT DISTINCT
	LN10.BF_SSN AS SSN,
	GR10.WF_GTR_RFR AS LOAN_ID,
	DW01.WC_DW_LON_STA AS LOAN_STATUS,
	dw01.WD_CLC_THU AS LOAN_STATUS_EFFECTIVE_DATE,
	LN10.LA_CUR_PRI AS CURRENT_PRINCIPAL,
	dw01.WA_TOT_BRI_OTS AS CURRENT_INTEREST,
	dw01.WD_CLC_THU AS CURRENT_BALANCE_EFFECTIVE_DATE
FROM 
	@DATA D
	INNER JOIN UDW..GR10_RPT_LON_APL GR10
		ON GR10.BF_SSN = D.BF_SSN
		AND GR10.WF_GTR_RFR = D.LOAN_ID
	INNER JOIN UDW..LN10_LON LN10
		ON LN10.BF_SSN = D.BF_SSN
		AND LN10.[LF_GTR_RFR] = D.LOAN_ID
	INNER JOIN UDW..DW01_DW_CLC_CLU DW01
		ON DW01.BF_SSN = LN10.BF_SSN
		AND DW01.LN_SEQ = LN10.LN_SEQ
