DECLARE @DATA TABLE( BF_SSN CHAR(9))



SELECT DISTINCT
	LN80.BF_SSN,
	LN80.LN_SEQ,
	CONVERT(VARCHAR(10), LN80.LD_BIL_CRT, 101) AS BILL_CREATE_DATE,
	LN80.LN_SEQ_BIL_WI_DTE,
	LN80.LN_BIL_OCC_SEQ,
	(LN80.LA_BIL_CUR_DU + ISNULL(LN80.LA_BIL_PAS_DU,0.00)) AS AMOUNT_DUE_ON_BILL,
	LN66.LA_RPS_ISL AS REPAYMENT_SCHEDULE_AMOUNT,
	LN65.LC_TYP_SCH_DIS AS SCHEDULE_TYPE
FROM
	@DATA D
	INNER JOIN UDW..LN80_LON_BIL_CRF LN80
		ON LN80.BF_SSN = D.BF_SSN
	INNER JOIN UDW..BL10_BR_BIL BL10
		ON BL10.BF_SSN = LN80.BF_SSN
		AND BL10.LD_BIL_CRT = LN80.LD_BIL_CRT
		AND BL10.LN_SEQ_BIL_WI_DTE = LN80.LN_SEQ_BIL_WI_DTE
	INNER JOIN UDW..LN65_LON_RPS LN65
		ON LN65.BF_SSN = LN80.BF_SSN
		AND LN80.LN_SEQ = LN65.LN_SEQ
		AND LN80.LD_BIL_CRT BETWEEN LN65.LD_CRT_LON65 AND CASE WHEN CAST(LN65.LF_LST_DTS_LN65 AS DATE) = CAST(LN65.LD_CRT_LON65 AS DATE) and LN65.LF_LST_DTS_LN65 > '01/01/2019' THEN GETDATE() ELSE LN65.LF_LST_DTS_LN65 END
	INNER JOIN UDW..LN66_LON_RPS_SPF LN66
		ON LN66.BF_SSN = LN65.BF_SSN
		AND LN66.LN_SEQ = LN65.LN_SEQ
		AND LN66.LN_RPS_SEQ = LN65.LN_RPS_SEQ
		AND LN66.LN_GRD_RPS_SEQ = 1
WHERE
	LN80.LC_STA_LON80 = 'A'
	AND BL10.LC_STA_BIL10 = 'A'
	AND LN80.LD_BIL_CRT > '03/01/19'
	AND LN65.LC_TYP_SCH_DIS NOT IN  ('IB','IL')
	AND LN80.LC_BIL_TYP_LON = 'P'
ORDER BY
	BF_SSN,
	LN_SEQ,
	BILL_CREATE_DATE
