DECLARE @Temp TABLE(BF_SSN VARCHAR(9), LN_SEQ INT, CorrectDate DATE)

SELECT DISTINCT
	T.*,
	LN40.LD_SBM_CLM_PCL,
	CASE WHEN DATEDIFF(DAY,T.CorrectDate,LN40.LD_SBM_CLM_PCL) <= 30 THEN 'Within 30' ELSE 'Greater Than 30' END 
FROM 
@Temp T
INNER JOIN UDW..LN10_LON LN10
	ON LN10.BF_SSN = T.BF_SSN
	AND LN10.LN_SEQ = T.LN_SEQ
LEFT JOIN UDW..LN40_LON_CLM_PCL LN40
	ON LN40.BF_SSN = LN10.BF_SSN
	AND LN40.LN_SEQ = LN10.LN_SEQ
	AND LN40.LD_SBM_CLM_PCL IS NOT NULL
	AND LN40.LD_SBM_CLM_PCL >= T.CorrectDate