DECLARE @DATA TABLE(BF_SSN VARCHAR(9), LN_SEQ INT, LD_FAT_EFF DATE)

SELECT
	D.*,
	ABS(LN90.LA_FAT_NSI) AS AMOUNT_OF_TRANSACTION
FROM
	@DATA D
	LEFT JOIN UDW..LN90_FIN_ATY LN90
		ON LN90.BF_SSN = D.BF_SSN
		AND LN90.LN_SEQ = D.LN_SEQ
		AND LN90.LD_FAT_EFF = D.LD_FAT_EFF
		AND LN90.PC_FAT_TYP = '39'
		AND LN90.PC_FAT_SUB_TYP = '03'
		AND LN90.LC_STA_LON90 = 'A'
		AND ISNULL(LN90.LC_FAT_REV_REA,'') = ''
