USE ODW
GO

DECLARE @DATA TABLE (BF_SSN INT) --DOING INT ON THIS BECAUSE SOME OF THE LEADING 0'S ARE REMOVED







SELECT
	RIGHT('000000000' + CAST(D.BF_SSN AS VARCHAR(9)),9) AS SSN,
	COUNT(*) AS LOAN_COUNT,
	SUM(ISNULL(LA_CLM_BAL,0) + ISNULL(LA_CLM_INT_ACR,0)) AS PRINCIPAL_INTEREST
FROM
	@DATA D
	INNER JOIN ODW..DC02_BAL_INT DC02
		ON CAST(DC02.BF_SSN AS INT) = D.BF_SSN
GROUP BY
	D.BF_SSN