DECLARE @SEEDDATA TABLE (NUMBER INT, SSN CHAR(9))


SELECT DISTINCT
	SD.NUMBER,
	'CS',
	SD.SSN,
	CASE WHEN LN10.BF_SSN IS NULL THEN 'N' ELSE 'Y' END AS BORROWER_ON_SYSTEM,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE LTRIM(RTRIM(PD10.DM_PRS_1)) END AS FIRST_NAME,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE LTRIM(RTRIM(PD10.DM_PRS_LST)) END AS LAST_NAME,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE CONVERT(VARCHAR(10), PD10.DD_BRT, 101) END AS DOB,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE COALESCE(PH05.DX_CNC_EML_ADR, PD32.DX_ADR_EML,'')  END AS EMAIL,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE PD40.PHN END AS PHONE,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE PD30.DX_STR_ADR_1 + ' ' + PD30.DX_STR_ADR_2 END AS [ADDRESS],
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE PD30.DM_CT END AS CITY,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE PD30.DC_DOM_ST END AS [STATE],
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE PD30.DF_ZIP_CDE END AS ZIP,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE CASE WHEN FORB.BF_SSN IS NOT NULL THEN 'Yes' ELSE 'No' END END AS CURRENTLY_IN_FORB,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE '' END AS NOT_IN_FORB,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE 'N' END AS SENT_NON_0_BILL,  --USED ECorrFed..DocumentDetails JUST LOOKED UP THE 2 ACCOUNT MANUALLY AS THE JOIN ADDED A LOT OF TIME
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE CASE WHEN LN90.BF_SSN IS NOT NULL THEN 'Y' ELSE 'N' END END AS MADE_PAYMENT,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE '' END AS REFUNDED,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE CASE WHEN LN41.BF_SSN IS NOT NULL THEN 'Y' ELSE 'N' END END AS CREDIT_REPORTING,
	CASE WHEN LN10.BF_SSN IS NULL THEN '' ELSE '' END AS UPDATE_CREDIT_REPORTING
	
FROM
	@SEEDDATA SD
	LEFT JOIN CDW..LN10_LON LN10
		ON LN10.BF_SSN = SD.SSN
	LEFT JOIN CDW..PD10_PRS_NME PD10
		ON PD10.DF_PRS_ID = SD.SSN
	LEFT JOIN CDW..PD30_PRS_ADR PD30
		ON PD30.DF_PRS_ID = SD.SSN
	LEFT JOIN CDW..PH05_CNC_EML PH05
		ON PH05.DF_SPE_ID = PD10.DF_SPE_ACC_ID
		AND PH05.DI_VLD_CNC_EML_ADR = 'Y'
	LEFT OUTER JOIN 
	(
		SELECT
			*,
			ROW_NUMBER() OVER (PARTITION BY DF_PRS_ID ORDER BY PriorityNumber) [EmailPriority]
		FROM
		(
			SELECT
				PD32.DF_PRS_ID,
				PD32.DX_ADR_EML,
				CASE 
					WHEN DC_ADR_EML = 'H' THEN 1
					WHEN DC_ADR_EML = 'A' THEN 2
					WHEN DC_ADR_EML = 'W' THEN 3
				END [PriorityNumber]
			FROM
				CDW..PD32_PRS_ADR_EML PD32
			WHERE
				PD32.DI_VLD_ADR_EML = 'Y'
		) Email
	) PD32 
		ON PD32.DF_PRS_ID = SD.SSN
		AND PD32.EmailPriority = 1 --highest priority email only
LEFT JOIN 
(
	SELECT
		PD40.DF_PRS_ID,
		COALESCE(PD40.DN_DOM_PHN_ARA,'') + COALESCE(PD40.DN_DOM_PHN_XCH,'') + COALESCE(PD40.DN_DOM_PHN_LCL,'') AS PHN,
		/*Rank phones by category and type*/
		DENSE_RANK() OVER (PARTITION BY PD40.DF_PRS_ID ORDER BY Ranks.TYPERANK) AS OverallRank
	FROM
		CDW..PD40_PRS_PHN PD40
		INNER JOIN
		(
			SELECT
				*,
				CASE WHEN DC_PHN = 'H' THEN 1
						WHEN DC_PHN = 'A' THEN 2
						WHEN DC_PHN = 'W' THEN 3
						WHEN DC_PHN = 'M' THEN 4
				END AS TypeRank --H A W M TYPE
						
			FROM
				CDW..PD40_PRS_PHN PD40
			WHERE
				PD40.DI_PHN_VLD = 'Y'
				AND PD40.DC_NO_HME_PHN != 'J'
		) Ranks
			ON Ranks.DF_PRS_ID = PD40.DF_PRS_ID
			AND Ranks.DC_PHN = PD40.DC_PHN
			AND Ranks.DC_ALW_ADL_PHN = PD40.DC_ALW_ADL_PHN
	WHERE
		PD40.DI_PHN_VLD = 'Y'
				
) PD40
	ON PD40.DF_PRS_ID = SD.SSN
	AND PD40.OverallRank = 1
LEFT JOIN
(
	SELECT	DISTINCT
		FB10.BF_SSN,
		max(LN60.LD_FOR_BEG) as LD_FOR_BEG,
		max(LN60.LD_FOR_END) as LD_FOR_END
	FROM	
		CDW..FB10_BR_FOR_REQ FB10
		INNER JOIN CDW..LN60_BR_FOR_APV LN60
			ON LN60.BF_SSN = FB10.BF_SSN
			AND LN60.LF_FOR_CTL_NUM = FB10.LF_FOR_CTL_NUM
	WHERE
		FB10.LC_FOR_STA = 'A'
		AND LN60.LC_STA_LON60 = 'A'
		AND FB10.LC_STA_FOR10 = 'A'
		--AND FB10.LC_FOR_TYP = '05'
		AND GETDATE() BETWEEN LN60.LD_FOR_BEG AND LN60.LD_FOR_END
	group by fb10.bf_ssn
)FORB
	ON FORB.BF_SSN = SD.SSN
LEFT JOIN
(
	SELECT DISTINCT
		BF_SSN
	FROM
		CDW..LN90_FIN_ATY
	WHERE
		PC_FAT_TYP = '10'
		and PC_FAT_SUB_TYP = '10'
		AND LD_FAT_EFF > '05/18/2018'	
) LN90
	ON LN90.BF_SSN = SD.SSN
LEFT JOIN OPENQUERY(LEGEND,
'
SELECT
	*
FROM
	PKUB.LN41_LON_CRB_RPT
WHERE
	BF_SSN IN (''313940287'',''030540730'')
	AND LC_RPT_STA_CRB != ''11''
	AND LF_CRT_DTS_LN41 > ''05/25/2018'' 
') LN41
	ON LN41.BF_SSN = SD.SSN
	
ORDER BY
	CASE WHEN LN10.BF_SSN IS NULL THEN 'N' ELSE 'Y' END  DESC