DECLARE @DATA TABLE (BF_SSN INT)






SELECT DISTINCT
DC02.BF_SSN,
COUNT(*) AS LOAN_COUNT,
SUM(ISNULL(LA_CLM_BAL,0.00) - ISNULL(LA_CLM_PRJ_COL_CST,0.00)) AS PRINCIPAL_INTEREST
FROM
@DATA D
INNER JOIN ODW..DC02_BAL_INT DC02
ON CAST(DC02.BF_SSN AS INT) = D.BF_SSN
AND DC02.LA_CLM_BAL > 0
GROUP BY
DC02.BF_SSN